# Write your MySQL query statement below
select Trips.request_at as 'Day', round(count(case when (Trips.status ='cancelled_by_driver' or status ='cancelled_by_client') and u1.banned = 'No' and u2.banned='No' then 1 end)/count(case when u1.banned='No' and u2.banned='No' then 1 end) ,2) as 'Cancellation Rate' from Trips left join Users as u1 on Trips.client_id = u1.users_id left join Users as u2 on Trips.driver_id = u2.users_id group by Trips.request_at having Trips.request_at >= '2013-10-01' and Trips.request_at <= '2013-10-03' and count(case when u1.banned='No' and u2.banned='No' then 1 end) >0 ; 